import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { navigate } from '@reach/router'

const Edit = (props) => {

    const [petInfo, setPetInfo] = useState({
        name: "",
        type: "",
        description: "",
        skill1: "",
        skill2: "",
        skill3: ""
    })

    useEffect(() => {
        axios.get(`http://localhost:8000/pets/info/${props.id}`)
            .then(res => {
                console.log(res)
                setPetInfo(res.data.results)

            })
            .catch(err => console.log(err))
    }, [])

    const changehandler = (e) => {
        console.log("changing something!")
            setPetInfo({
                ...petInfo,
                [e.target.name]: e.target.value
            })
    }


    const submithandler = (e) => {
        e.preventDefault();
        axios.put(`http://localhost:8000/pets/edit/${props.id}`, petInfo)
            .then(res => {
                console.log("******updating*****")
                console.log(res)
                navigate("/")
            })
            .catch(err => console.log(err))
    }

    return (
        <div>
            <h1>Editing pet number: {props.id} </h1>
            <form onSubmit={submithandler}>
                <div className="form-group">
                    <label>Petd By(Author):</label>
                    <input onChange={changehandler} type="text" name="author" id="" className="form-control" value={petInfo.author} />
                    {/* {formErrors.author? <p className="text-danger">{formErrors.author.message}</p>:""} */}
                </div>
                <div className="form-group">
                    <label>Content</label>
                    <textarea onChange={changehandler} name="content" id="" cols="30" rows="10" className="form-control" value={petInfo.content}></textarea>
                    {/* {formErrors.content? <p className="text-danger">{formErrors.content.message}</p>: ""} */}

                </div>
                <div className="form-group">
                    <label>Petd on:</label>
                    <input onChange={changehandler} type="date" name="petdOn" id="" className="form-control"/>
                </div>
                <div className="form-group">
                    <label>Is Motivational?</label>
                    <input onChange={changehandler} checked={petInfo.isMotivational} type="checkbox" name="isMotivational" id="" />
                </div>
                <input type="submit" value="Update Pet!" />
            </form>
        </div>
    );
};


export default Edit;
