import React, {useState, useEffect} from 'react';
import axios from 'axios';
import { navigate } from '@reach/router';



const PetDetail = (props) => {

    const [petInfo, setPetInfo] = useState({})

    useEffect(()=>{
        axios.get(`http://localhost:8000/pets/${props.id}`)
            .then(res=> {
                console.log("Getting one pet")
                console.log(res.data.results)
                setPetInfo(res.data.results)
            })
            .catch(err=> console.log("error", err))
    },[])


    const deletePet =(e)=>{
        console.log("deleting something")
        axios.delete(`http://localhost:8000/pets/${props.id}`)
            .then(res=>{
                console.log("Adopting Pet")
                console.log(res)
                navigate("/")
            })
            .catch(err=> console.log("errorrrrrr", err))
    }
    
    return (
        <div>
            <h4>Details about: {petInfo.name}</h4>
            
            <p>Id of the pet: {props.id} </p>
            <p>Pet Author: {petInfo.name}</p>
            <p>Pet Content: {petInfo.content}</p>
            <p>Petd on this date: {petInfo.petdOn}</p>
            <p>Is it motivational? {petInfo.isMotivational? "Yes": "No"}</p>
            <button onClick= {deletePet} className="btn btn-danger">Delete</button>

        </div>
    );
};


export default PetDetail;
