import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from '@reach/router';

const AllPets = () => {
    const [allPets, setAllPets] = useState([])
    const [deleteclicked, setDeleteClicked] = useState(false)

    useEffect(() => {
        axios.get("http://localhost:8000/pets")
            .then(res => {
                console.log("logging response!!", res)
                setAllPets(res.data.results)

            })
            .catch(err => console.log("errorrr with axios call", err))
    }, [deleteclicked])


    const deletePet = (e, petid) => {
        console.log("deleting the pet thooooo")
        console.log(petid)
        axios.delete(`http://localhost:8000/api/pets/${petid}`)
            .then(res => {
                console.log("*******")
                console.log(res)
                console.log("*******")
                setDeleteClicked(!deleteclicked)

            })
            .catch(err => console.log(err))

    }

    return (
        <div>
            <h1>These pets are looking for a good home</h1>

            <table class="table table-striped container border border-dark">
                <thead>
                    <tr>
                        <th scope="col">First</th>
                        <th scope="col">First</th>
                        <th scope="col">Last</th>
                    </tr>
                </thead>

                <tbody className>
                    {allPets.map((q, i) => {
                        return <tr key={i}>

                            <td>{q.name}</td>
                            <td>{q.type}</td>
                            <td>
                                <Link to={`/pets/info/${q._id}`} className="btn btn-primary m-2">More Info</Link>
                                <Link to={`/pets/edit/${q._id}`} className="btn btn-info m-2">Edit</Link>
                                <button onClick={(e) => deletePet(e, q._id)} className="btn btn-danger">Adopt</button>
                            </td>
                        </tr>
                    })}

                </tbody>
            </table>
        </div>
    );
};

export default AllPets;
