import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { navigate } from '@reach/router'

const Edit = (props) => {

    const [petInfo, setPetInfo] = useState({
        name: "",
        type: "",
        description: "",
        skill1: "",
        skill2: "",
        skill3: ""
    })

    useEffect(() => {
        axios.get(`http://localhost:8000/pets/${props.id}`)
            .then(res => {
                console.log(res)
                setPetInfo(res.data.results)

            })
            .catch(err => console.log(err))
    }, [])

    const changeHandler = (e) => {
        console.log("changing something!")
            setPetInfo({
                ...petInfo,
                [e.target.name]: e.target.value
            })
    }


    const submitHandler = (e) => {
        e.preventDefault();
        axios.put(`http://localhost:8000/pets/edit/${props.id}`, petInfo)
            .then(res => {
                console.log("******updating*****")
                console.log(res)
                navigate("/")
            })
            .catch(err => console.log(err))
    }

    return (
        <div>
            <h1>Edit: {petInfo.name}</h1>

            <form onSubmit={submitHandler} className='row border p-3'>

                <div className="left col-2">

                    <div className="form-group">
                        <label>Pet Name:</label>
                        <input onChange={changeHandler} type="text" name="name" id="" className="form-control" placeholder={petInfo.name}/>
                    </div>

                    <div className="form-group">
                        <label>Pet Type:</label>
                        <input onChange={changeHandler} type="text" name="type" id="" className="form-control" placeholder={petInfo.type}/>
                    </div>

                    <div className="form-group">
                        <label>Pet Description</label>
                        <textarea onChange={changeHandler} name="description" id="" cols="10" rows="10" className="form-control" placeholder={petInfo.description}></textarea>
                    </div>

                </div>


                <div className="right col-2">
                    <p>Skills: (optional) </p>
                    <div className="form-group">
                        <label>Skill: </label>
                        <input onChange={changeHandler} type="text" name="skill1" id="" className="form-control" placeholder={petInfo.skill1}/>
                    </div>

                    <div className="form-group">
                        <label>Skill:</label>
                        <input onChange={changeHandler} type="text" name="skill2" id="" className="form-control" placeholder={petInfo.name}/>
                    </div>

                    <div className="form-group">
                        <label>Skill:</label>
                        <input onChange={changeHandler} type="text" name="skill3" id="" className="form-control" />
                    </div>


                    <input type="submit" value="Add Pet!" className="mt-3 btn btn-primary"/>
                </div>

            </form>
        </div>
    );
};


export default Edit;
